<!DOCTYPE html>
<html>
    <head>
	    <title>Index of <?php echo urldecode($path);?></title>
        <meta charset="utf-8">
        <style>
		*{box-sizing:border-box}h1{border-bottom:1px solid silver;margin-bottom:10px;padding-bottom:10px;white-space:nowrap}table{border-collapse:collapse;font-family:Consolas,monaco,monospace}th{font-weight:700}.file-name{text-align:left}.file-size{padding-left:4em}.file-date-created,.file-date-modified{padding-left:2em}.file-date-created,.file-date-modified,.file-size{text-align:end;white-space:nowrap}.icon{padding-left:1.5em;text-decoration:none}.icon:hover{text-decoration:underline}.icon-file{background:url(data:image/png;base64,) left top no-repeat}.icon-dir{background:url(data:image/png;base64,) left top no-repeat}.icon-up{background:url(data:image/png;base64,) left top no-repeat}
        </style>
    </head>
    <body>
		<h1 id="heading">Index of <?php echo urldecode($path);?></h1>
		<table id="table">
			<tr><th class="file-name">Name</th><th class="file-size">Size</th><th class="file-date-created">Date Created</th><th class="file-date-modified">Date Modified</th></tr>
			<?php if($path != '/'):?>
				<tr>
					<td class="file-name">
						<a class="icon icon-up" href="<?php echo get_absolute_path($root.$path.'../');?>">..</a>
					</td>
					<td class="file-size"></td>
					<td class="file-date-modified"></td>
				</tr>
			<?php endif;?>
			<?php foreach((array)$items as $item):?>
				<?php if(!empty($item['folder'])):?>
					<tr>
						<td class="file-name"><a class="icon icon-dir" href="<?php echo get_absolute_path($root.$path.rawurlencode($item['name']));?>"><?php echo $item['name'];?>/</a></td>
						<td class="file-size"><?php echo onedrive::human_filesize($item['size']);?></td>
						<td class="file-date-modified"><?php echo date("Y-m-d H:i:s", $item['lastModifiedDateTime']);?></td>
					</tr>
				<?php else:?>
					<tr>
						<td class="file-name"><a class="icon icon-file" href="<?php echo get_absolute_path($root.$path).rawurlencode($item['name']);?>"><?php echo $item['name'];?></a></td>
						<td class="file-size"><?php echo onedrive::human_filesize($item['size']);?></td>
						<td class="file-date-modified"><?php echo date("Y-m-d H:i:s", $item['lastModifiedDateTime']);?></td>
					</tr>
				<?php endif;?>
			<?php endforeach;?>
		</table>
    </body>
</html>